# alternatively
safe_div &lt;- function(a, b) {
  if(any(b == 0)) {
    warning("denominator is 0; returning NA")
  }

  x &lt;- rep(NA_real_, length(a))
  x[b != 0] &lt;- a[b != 0] / b[b != 0]

  return(x)
}




import math, warnings

def safe_div(a, b):
    try:
        return a / b
    except ZeroDivisionError:
        warnings.warn("denominator is 0; returning NaN")
        return math.nan

print(safe_div(1,1))
print(safe_div(2,0))
